 \begin{tikzpicture}[thick,scale=1.25] [domain=-3:3]
\draw[step=1cm,color=gray!25] (-2,-2) grid (4,4);
   \draw [black!80,line width=0.5pt,-latex] (0,0) -- (4,0) node [below] at (3.5,0) {$x$};
   \draw [black!80,line width=0.5pt,-latex] (0,0) -- (-2,0);
   \draw [black!80,line width=0.5pt,-latex] (0,0) -- (0,4) node [right] at (0,3.5) {$y$};
   \draw [black!80,line width=0.5pt,-latex] (0,0) -- (0,-2);
\draw[color=linecolor, line width=1.5pt,domain=-1.25:1.25,smooth]   plot (\x,{\x*\x*\x+\x+1}) node [right] at (1.5,3.75) {$y=f(x)$};
\fill (1,3) circle (3pt) node [right] (1,3.25) {$(1,3)$};
\foreach \x/\xtext in {  -1/-1, 1/1, 2/2, 3/3}
    \draw[shift={(\x,0)}] (0pt,3pt) -- (0pt,-3pt) node[below] {$\xtext$};
\foreach \y/\ytext in {-1/-1, 1/1, 2/2, 3/3}
    \draw[shift={(0,\y)}] (-2pt,0pt) -- (2pt,0pt) node[left] {$\ytext$};
\end{tikzpicture}

